        income_store: function (ward_bm_income_category_id, amount) {
            const month = this.$route.params.month;
            const formData = {
                ward_bm_income_category_id: ward_bm_income_category_id,
                amount: amount,
                month: month,
            };
            axios
                .post("/ward-bm-income/store", formData)
